CKEDITOR.plugins.setLang("a11yhelp","ca",{title:"Instruccions d'Accessibilitat",contents:"Continguts de l'Ajuda. Per tancar aquest quadre de di\xe0leg premi ESC.",legend:[{name:"General",items:[{name:"Editor de barra d'eines",legend:"Premi ${toolbarFocus} per despla\xe7ar-se per la barra d'eines. Vagi en el seg\xfcent i anterior grup de barra d'eines amb TAB i SHIFT-TAB. Vagi en el seg\xfcent i anterior bot\xf3 de la barra d'eines amb RIGHT ARROW i LEFT ARROW. Premi SPACE o ENTER per activar el bot\xf3 de la barra d'eines."},{name:"Editor de quadre de di\xe0leg",legend:"Dins d'un quadre de di\xe0leg, premi la tecla TAB per despla\xe7ar-se al seg\xfcent camp del quadre de di\xe0leg, premi SHIFT + TAB per despla\xe7ar-se a l'anterior camp, premi ENTER per acceptar el quadre de di\xe0leg, premi ESC per cancel\xb7lar el quadre de di\xe0leg. Per els quadres de di\xe0leg que tenen diverses pestanyes, premi ALT + F10 per anar a la llista de pestanyes. Despr\xe9s podr\xe0 despla\xe7ar-se a la seg\xfcent pestanya amb TAB o RIGHT ARROW. Anar a la pestanya anterior amb SHIFT + TAB o LEFT ARROW. Premi SPACE o ENTER per seleccionar la pestanya."},{name:"Editor de men\xfa contextual",legend:"Premi ${contextMenu} o APPLICATION KEY per obrir el men\xfa contextual. Despr\xe9s desplacis a la seg\xfcent opci\xf3 del men\xfa amb TAB o DOWN ARROW. Desplacis a l'anterior opci\xf3 amb SHIFT+TAB o UP ARROW. Premi SPACE o ENTER per seleccionar l'opci\xf3 del men\xfa. Obri el submen\xfa de l'actual opci\xf3 utilitzant SPACE o ENTER o RIGHT ARROW. Pot tornar a l'opci\xf3 del men\xfa pare amb ESC o LEFT ARROW. Tanqui el men\xfa contextual amb ESC."},{name:"Editor de caixa de llista",legend:"Dins d'un quadre de llista, desplacis al seg\xfcent element de la llista amb TAB o DOWN ARROW. Desplacis a l'anterior element de la llista amb SHIFT + TAB o UP ARROW. Premi SPACE o ENTER per seleccionar l'opci\xf3 de la llista. Premi ESC per tancar el quadre de llista."},{name:"Editor de barra de ruta de l'element",legend:"Premi ${elementsPathFocus} per anar als elements de la barra de ruta. Desplacis al bot\xf3 de l'element seg\xfcent amb TAB o RIGHT ARROW. Desplacis a l'anterior bot\xf3 amb  SHIFT+TAB o LEFT ARROW. Premi SPACE o ENTER per seleccionar l'element a l'editor."}]},{name:"Ordres",items:[{name:"Desfer ordre",legend:"Premi ${undo}"},{name:"Refer ordre",legend:"Premi ${redo}"},{name:"Ordre negreta",legend:"Premi ${bold}"},{name:"Ordre cursiva",legend:"Premi ${italic}"},{name:"Ordre subratllat",legend:"Premi ${underline}"},{name:"Ordre enlla\xe7",legend:"Premi ${link}"},{name:"Ordre amagar barra d'eines",legend:"Premi ${toolbarCollapse}"},{name:"Ordre per accedir a l'anterior espai enfocat",legend:"Premi ${accessPreviousSpace} per accedir a l'enfocament d'espai m\xe9s proper inabastable abans del s\xedmbol d'intercalaci\xf3, per exemple: dos elements HR adjacents. Repetiu la combinaci\xf3 de tecles per arribar a enfocaments d'espais distants."},{name:"Ordre per accedir al seg\xfcent espai enfocat",legend:"Premi ${accessNextSpace} per accedir a l'enfocament d'espai m\xe9s proper inabastable despr\xe9s del s\xedmbol d'intercalaci\xf3, per exemple: dos elements HR adjacents. Repetiu la combinaci\xf3 de tecles per arribar a enfocaments d'espais distants."},{name:"Ajuda d'accessibilitat",legend:"Premi ${a11yHelp}"}]}]});